**Markov cahain Text generation code**#Packagesinstall.packages("markovchain")install.packages("tm")library(markovchain) library(tm)#Import Texttext <- readLines(file.choose())filePath <-"C:\\Users\\JosephN2\\Desktop\\That.txt"text <- readLines(filePath)#Text cleaningdocs <- Corpus(VectorSource(text))inspect(docs)toSpace <- content_transformer(function (x , pattern ) gsub(pattern, " ", x))docs <- tm_map(docs, toSpace, "/")docs <- tm_map(docs, toSpace, "@")docs <- tm_map(docs, toSpace, "\\|")docs <- tm_map(docs, toSpace, ")")docs <- tm_map(docs, toSpace, "?")# Remove punctuationsdocs <- tm_map(docs, removePunctuation)# Eliminate extra white spacesdocs <- tm_map(docs, stripWhitespace)docs <- tm_map(docs, removeNumbers)length(docs)#Split textterms <- unlist(strsplit(text, ' '))fit <- markovchainFit(data = terms) plot(fit$estimate) paste(markovchainSequence(n=15, markovchain=fit$estimate), collapse=' ')
